});

/* navbar mobile */
$(document).ready(function(){

    var menu = $(".navbar-nav");
    var hamburger = $(".navbar-toggler");
    var navbg = $(".nav-bg");
    var close = $(".nav-close");
    var overscrolly = $("body");
    var login = $("#nav-login");
    var menuOpen;

    function openMenu(){
      menu.css("left", "0px");
      navbg.css("display", "block");
      overscrolly.css("overflow","hidden")
      login.removeClass("button");
      menuOpen = true;
    }

    function closeMenu(){
      menu.css("left", "-320px");
      navbg.css("display", "none");
      overscrolly.css("overflow","scroll")
      menuOpen = false;
    }

    function toggleMenu(){
      if (menuOpen) {
        closeMenu();
      } else {
        openMenu();
      }
    }
        close.on({
            click: function(){
            toggleMenu();
            }
        });

        hamburger.on({
            click: function(){
            toggleMenu();
            }
        })

  });

/* dropdown nav mobile */
$(document).ready(function(){

  var active = false;
  var navbardrop = $("#sd-navbardrop")
  var icon = $(".dropdown-icon")
  var icon_active = $(".active .dropdown-icon")

      function openDrop(){

        navbardrop.addClass("active");
        active = true;
      }

      function closeDrop(){
        navbardrop.removeClass("active");
        active = false;
      }

    function toggleDrop(){
      if (active) {
        closeDrop();
      } else {
        openDrop();
      }
    }
        icon.on({
            click: function(){
            toggleDrop();
            }
        });
  });


